	public class MandatoryColumn
	{
		private String mColName;
		private String mMessage;

		public MandatoryColumn(String pColName)
		{
			mColName = pColName;
			mMessage = "Unable to insert or update to null a mandatory column :" + mColName;
		}
		private boolean CheckIns (Connection pConnection,ManagedEntity pManagedEntity)
		{
			return (pManagedEntity.isColumnDefined(mColName) && pManagedEntity.getColumnValue(mColName) != null);
		}
		private boolean CheckUpdByPk (Connection pConnection,ManagedEntity pManagedEntity)
		{
			return (!pManagedEntity.isColumnDefined(mColName) || pManagedEntity.getColumnValue(mColName) != null);
		}
		private boolean CheckUpdByFilter(Connection pConnection,UpdateFilter pUpdateFilter)
		{
			return (!pUpdateFilter.getManagedEntity().isColumnDefined(mColName) || pUpdateFilter.getManagedEntity().getColumnValue(mColName) != null);
		}
	}
	public class UniqueKey 
	{	

		private String mKeyName;
		private List mKeyColsName;
		private String mMessage;
		//private List mInsertColumns;

		public UniqueKey(String pKeyName, List pKeyColsName,String pMessage) 
		{
			mKeyName = pKeyName;
			mKeyColsName = pKeyColsName;
			mMessage = pMessage;
		}
		//CheckIns return true the key doesnt existe in the table else return false
		private boolean CheckIns(Connection pConnection,ManagedEntity pManagedEntity) throws DataServiceException
		{ 
			PreparedStatement st = null;
			ResultSet rs = null;
			String query = null;
		try{
			String whereClause = Utils.concatStrings(mKeyColsName, null, "=?", " AND ");
			query = Utils.createSelectQuery(mKeyColsName, FULLY_QUALIFIED_TABLE_NAME, whereClause, null);
			st = pConnection.prepareStatement(query);
			Utils.setParameters(getMetaData().getColumnsMeta(), st, pManagedEntity, mKeyColsName);
			rs = st.executeQuery();
			return rs.next();
			}
		catch (DataServiceException e) {
			throw e;
		}
		catch (SQLException e)
		{
			throw new DataServiceException(query, e);
		}

			finally
			{
				try {
					if (rs != null)
					{
						rs.close();
					}
					if (st != null)
					{
						st.close();
					}

				} catch (Exception e) {
					throw new DataServiceException(e);
				}
			}
		}

		private boolean CheckUpdByPk(Connection pConnection,ManagedEntity pManagedEntity) throws DataServiceException
		{ 
			PreparedStatement st = null;
			ResultSet rs = null;
			String query =null; 
		try{
			List definedUpdateCols = pManagedEntity.getDefinedColumns(UPDATE_COLUMNS);
			String pkCondition = Utils.concatStrings(PK_COLUMNS, null, "=?", " AND ");

					//Construction de selectUPDColumns
			StringBuffer stringBuffer = new StringBuffer();
			for (Iterator iter = TABLE_COLUMNS.listIterator() ; iter.hasNext() ;)
			{
				String colName = (String)iter.next();
				if (pManagedEntity.isColumnDefined(colName))
				{
					stringBuffer.append(" CASE WHEN NOT(" + pkCondition + ") THEN " + colName + " ELSE ? END ");
					stringBuffer.append(COL_ALIAS_WORD);
					stringBuffer.append(" ");
				}
				stringBuffer.append(colName);

				if (iter.hasNext())
				{
					stringBuffer.append(" , ");
				}
			}
			String selectUpdColumns = stringBuffer.toString();

			String keyColumns = Utils.concatStrings(mKeyColsName, null,null , " , ");		

			query = "select count(*) from ( select "
					+ selectUpdColumns 
					+ " from " 
					+ FULLY_QUALIFIED_TABLE_NAME
					+ " ) ws_alias group by "
					+ keyColumns
					+ " having count(*) > 1 ";

			st = pConnection.prepareStatement(query);
			List parameterList = new ArrayList();
			for (Iterator iter = definedUpdateCols.listIterator() ; iter.hasNext() ;)
			{
				parameterList.addAll(PK_COLUMNS);
				parameterList.add((String)iter.next());
			}
			Utils.setParameters(getMetaData().getColumnsMeta(), st, pManagedEntity, parameterList);
			rs = st.executeQuery();
			return rs.next();
			}

		catch (SQLException e)
		{
			throw new DataServiceException(query, e);
		}
		finally
			{
			try {
				if (rs != null)
				{
					rs.close();
				}
				if (st != null)
				{
					st.close();
				}

			} catch (Exception e) {
				throw new DataServiceException(e);
			}

			}
		}

		private boolean CheckUpdByFilter(Connection pConnection,UpdateFilter pUpdateFilter) throws DataServiceException
		{
			PreparedStatement st = null;
			ResultSet rs = null;
			String query = null;
		try{
			List definedUpdateCols = pUpdateFilter.getManagedEntity().getDefinedColumns(UPDATE_COLUMNS);
			String whereClause = Utils.createWhereClause(pUpdateFilter.getQueryFilter());
			if (whereClause ==null)
			{whereClause = "1=1";}

					//Construction de selectUPDColumns
			StringBuffer stringBuffer = new StringBuffer();
			for (Iterator iter = TABLE_COLUMNS.listIterator() ; iter.hasNext() ;)
			{
				String colName = (String)iter.next();
				if (pUpdateFilter.getManagedEntity().isColumnDefined(colName))
				{
					stringBuffer.append(" CASE WHEN NOT(" + whereClause + ") THEN " + colName + " ELSE ? END ");
					stringBuffer.append(COL_ALIAS_WORD);
					stringBuffer.append(" ");
				}
				stringBuffer.append(colName);

				if (iter.hasNext())
				{
					stringBuffer.append(" , ");
				}
			}
			String selectUpdColumns = stringBuffer.toString();	

			String keyColumns = Utils.concatStrings(mKeyColsName, null,null , " , ");		

			query = "select count(*) from ( select "
					+ selectUpdColumns 
					+ " from " 
					+ FULLY_QUALIFIED_TABLE_NAME
					+ " ) "
					+ TAB_ALIAS_WORD
					+" "
					+ ALIAS_TABLE_NAME
					+" group by "
					+ keyColumns
					+ " having count(*) > 1 ";



			st = pConnection.prepareStatement(query);
			List parameterList;
			int nextParam = 1;
			for (Iterator iter = definedUpdateCols.listIterator() ; iter.hasNext() ;)
			{
				// Set parameters for where condition
				Utils.setParameters(
					getMetaData().getColumnsMeta(), 
					st, 
					pUpdateFilter.getQueryFilter().getWhereEntity(), 
					pUpdateFilter.getQueryFilter().getWhereEntity().getDefinedColumns(), 
					nextParam
				);
				nextParam+=pUpdateFilter.getQueryFilter().getWhereEntity().getDefinedColumns().size();

				// Set parameter for column update value
				parameterList = new ArrayList();
				parameterList.add((String)iter.next());			
				Utils.setParameters(
					getMetaData().getColumnsMeta(), 
					st, 
					pUpdateFilter.getManagedEntity(), 
					parameterList, 
					nextParam
				);
				nextParam+=1;
			}

			rs = st.executeQuery();
			return rs.next();
			}

		catch (SQLException e)
		{
			throw new DataServiceException(query, e);
		}
			finally
			{
				try {
					if (rs != null)
					{
						rs.close();
					}
					if (st != null)
					{
						st.close();
					}

				} catch (Exception e) {
					throw new DataServiceException(e);
				}
			}
		}

	}
	public class Constraint
	{	
		private String mName;
		private String mMessage;
		private String mCondition;
		//private List mInsertColumns;

		public Constraint(String pName, String pCondition, String pMessage)
		{
			mName = pName;
			mCondition = pCondition;
			mMessage = pMessage;
		}


		private boolean CheckIns(Connection pConnection, ManagedEntity pManagedEntity)  throws DataServiceException
		{ 
			PreparedStatement st = null;
			ResultSet rs = null;
			String query = null;
		try{
			List definedInsertedCols = pManagedEntity.getDefinedColumns(INSERT_COLUMNS);

					//Construction de selectUPDColumns
			StringBuffer stringBuffer = new StringBuffer();
			for (Iterator iter = TABLE_COLUMNS.listIterator() ; iter.hasNext() ;)
			{
				String colName = (String)iter.next();
				if (pManagedEntity.isColumnDefined(colName))
				{
					stringBuffer.append(" case when 0=1 then S." + colName + " else ? end ");
				}
				else
				{
					stringBuffer.append(" case when 0=1 then S." + colName + " else null end ");
				}
				stringBuffer.append(COL_ALIAS_WORD);
				stringBuffer.append(" ");

				stringBuffer.append(colName);

				if (iter.hasNext())
				{
					stringBuffer.append(" , ");
				}
			}
			String selectInsColumns = stringBuffer.toString();

			query = "select 1 from ( "
					+" select "
					+  selectInsColumns
					+" from " 
					+ FULLY_QUALIFIED_TABLE_NAME
					+ TAB_ALIAS_WORD
					+" S "
					+" full outer join sysibm.sysdummy1"
					+" on (1=1) "
					+"fetch first 1 rows only"
					+ " ) "
					+ TAB_ALIAS_WORD
					+" "
					+ ALIAS_TABLE_NAME
					+ " where not "
					+ mCondition;

			st = pConnection.prepareStatement(query);
			Utils.setParameters(getMetaData().getColumnsMeta(), st, pManagedEntity, definedInsertedCols);
			rs = st.executeQuery();
			return rs.next();
			}


		catch (SQLException e)
		{
			throw new DataServiceException(query, e);
		}
			finally
			{
				try {
					if (rs != null)
					{
						rs.close();
					}
					if (st != null)
					{
						st.close();
					}

				} catch (Exception e) {
					throw new DataServiceException(e);
				}
			}
		}

		private boolean CheckUpdByPk(Connection pConnection, ManagedEntity pManagedEntity) throws DataServiceException
		{ 
			PreparedStatement st = null;
			ResultSet rs = null;
			String query= null;
		try{
			List definedUpdateCols = pManagedEntity.getDefinedColumns(UPDATE_COLUMNS);


					//Construction de selectUPDColumns
			StringBuffer stringBuffer = new StringBuffer();
			for (Iterator iter = TABLE_COLUMNS.listIterator() ; iter.hasNext() ;)
			{
				String colName = (String)iter.next();
				if (pManagedEntity.isColumnDefined(colName))
				{
					stringBuffer.append(" CASE WHEN 1=0 THEN " + colName + " ELSE ? END ");
					stringBuffer.append(COL_ALIAS_WORD);
					stringBuffer.append(" ");
				}
				stringBuffer.append(colName);

				if (iter.hasNext())
				{
					stringBuffer.append(" , ");
				}
			}
			String selectUpdColumns = stringBuffer.toString();


			String whereClause = Utils.concatStrings(PK_COLUMNS, null, "=?", " AND ");

			query = "select 1 from ( select "
					+ selectUpdColumns 
					+ " from "
					+ FULLY_QUALIFIED_TABLE_NAME
					+ " where "
					+ whereClause
					+ " ) "
					+ TAB_ALIAS_WORD
					+ " "
					+ ALIAS_TABLE_NAME 
					+ " where not("
					+ mCondition
					+ ")";

			st = pConnection.prepareStatement(query);
			List parameterList = new ArrayList(definedUpdateCols);

			parameterList.addAll(PK_COLUMNS);

			Utils.setParameters(getMetaData().getColumnsMeta(), st, pManagedEntity, parameterList);
			rs = st.executeQuery();
			return rs.next();
			}

		catch (SQLException e)
		{
			throw new DataServiceException(query, e);
		}
			finally
			{
				try {
					if (rs != null)
					{
						rs.close();
					}
					if (st != null)
					{
						st.close();
					}

				} catch (Exception e) {
					throw new DataServiceException(e);
				}	
			}
		}

		private boolean CheckUpdByFilter(Connection pConnection,UpdateFilter pUpdateFilter) throws DataServiceException 
		{ 
			PreparedStatement st = null;
			ResultSet rs = null;
			String query = null;
		try{
			List definedUpdateCols = pUpdateFilter.getManagedEntity().getDefinedColumns(UPDATE_COLUMNS);

			//Construction de selectUPDColumns

			StringBuffer stringBuffer = new StringBuffer();
			for (Iterator iter = TABLE_COLUMNS.listIterator() ; iter.hasNext() ;)
			{
				String colName = (String)iter.next();
				if (pUpdateFilter.getManagedEntity().isColumnDefined(colName))
				{
					stringBuffer.append(" CASE WHEN 1=0 THEN " + colName + " ELSE ? END ");
					stringBuffer.append(COL_ALIAS_WORD);
					stringBuffer.append(" ");
				}
				stringBuffer.append(colName);

				if (iter.hasNext())
				{
					stringBuffer.append(" , ");
				}
			}
			String selectUpdColumns = stringBuffer.toString();

			String whereClause = Utils.createWhereClause(pUpdateFilter.getQueryFilter());

			if (whereClause ==null)
			{whereClause = "1=1";}

			query = "select 1 from ( select "
					+ selectUpdColumns 
					+ " from " 
					+ FULLY_QUALIFIED_TABLE_NAME
					+ " where "
					+ whereClause
					+ " ) "
					+ TAB_ALIAS_WORD
					+ " "
					+ ALIAS_TABLE_NAME 
					+ " where not("
					+ mCondition
					+ ")";		
			st = pConnection.prepareStatement(query);

			Utils.setParameters(getMetaData().getColumnsMeta(), st, pUpdateFilter.getManagedEntity(), definedUpdateCols);

			Utils.setParameters(getMetaData().getColumnsMeta(), st, pUpdateFilter.getQueryFilter().getWhereEntity(), pUpdateFilter.getQueryFilter().getWhereEntity().getDefinedColumns(), definedUpdateCols.size() + 1);


			rs = st.executeQuery();
			return rs.next();
			}



		catch (SQLException e)
		{
			throw new DataServiceException(query, e);
		}

			finally
			{
				try {
					if (rs != null)
					{
						rs.close();
					}
					if (st != null)
					{
						st.close();
					}

				} catch (Exception e) {
					throw new DataServiceException(e);
				}
			}
		}

	}


